INSERT INTO marcas_postpage VALUES(14,replace(replace('**INFORMACION DEL PRODUCTO**\r\nINVIMA 2008 M-010211 R2\r\n\r\nBicarbonato de sodio.\r\n\r\nSal de Frutas LUA Plus alivia la indigestin y el malestar estomacal ocasionado por el consumo de comidas y bebidas en exceso.\r\n\r\nAnticido.\r\n\r\nAlivio de los sntomas de la indigestin, pesadez y llenura.\r\n\r\nOral\r\n\r\nAdultos y nios mayores de 12 aos: Un sobre de Sal de Frutas LUA, diluido en medio vaso con agua, 20 a 60 minutos despus de las comidas.\r\n\r\nPlegadiza x 8 y x 20 sobres','\r',char(13)),'\n',char(10)),'2020-07-06 14:16:00',replace(replace('Cada sobre de 5 g de Sal de Frutas LUA Plus contiene Bicarbonato de sodio 2.83 g; excipientes c.s.\r\n\r\nSabores:\r\nNaranja, Limon y el original. \r\n\r\nHipersensibilidad a la frmula. Sntomas de apendicitis o de dolores abdominales de tipologa no determinada y dems estados dolorosos e inflamatorios del aparato digestivo.\r\n\r\nInsuficiencia cardiaca o renal crnica, cirrosis heptica, hipertensin arterial. Mujeres en embarazo y en periodo de lactancia.','\r',char(13)),'\n',char(10)),20);
